import axios from "axios";
import React from "react";
import Dropdown from "../dropdown/dropdown.component";

export class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            countries: [],
            userCountry: ''
        }
    }
    componentDidMount (){
        this.getLocales()
        // const proxyUrl = 'https://cors-anywhere.herokuapp.com/'
        // const targetUrl = 'http://partners.api.skyscanner.net/apiservices/reference/v1.0/locales?apikey=prtl6749387986743898559646983194'
        // fetch(proxyUrl + targetUrl ,{
        //     headers : { 
        //         'Content-Type': 'application/json',
        //         'Accept': 'application/json'
        //        }})
        // .then(resp => resp.json())
        // .then(data =>)
    }
    async getLocales () {
        const proxyUrl = 'https://cors-anywhere.herokuapp.com/',
        targetUrl = 'http://partners.api.skyscanner.net/apiservices/reference/v1.0/locales?apikey=prtl6749387986743898559646983194'
        const res = await axios.get(proxyUrl + targetUrl)
        const data = res.data
        const newArray = Object.values(data)
        this.setState({countries: newArray})
        console.log(this.state.countries[0])
    }
    resetThenSet = ( id ) => {
        let country
        const temp = this.state.countries[0];
        console.log(temp)

        temp.forEach((item) => item.Name = true);
        temp[id].Name = true


        this.setState({
            countries: temp,
        })

    }

    render () {
        return (
            <article className="br2 ba dark-gray b--black-10 mv4 w-100 w-50-m w-25-l mw5 center shadow-5">
                <main className="pa4 black-80">
                    <div className="measure" accept-charset="utf-8">
                            <fieldset id="sign_up" class="ba b--transparent ph0 mh0">
                            <legend className="ph0 mh0 fw6 clip">Sign Up</legend>
                            <div className="mt3">
                            <div className="mt3">
                                    <label className="db fw1 lh-copy f6" htmlFor="email-address">Name</label>
                                    <input className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                                        type="text" 
                                        name="name"  
                                        id="name"
                                    />
                                </div>
                                <div>
                                    <Dropdown title = 'Select your country' 
                                        countries={this.state.countries[0]}
                                        resetThenSet={this.resetThenSet}
                                    />
                                </div>
                                <label className="db fw4 lh-copy f6" htmlFor="email-address">Email address</label>
                                <input className="pa2 input-reset ba bg-transparent w-100 measure" 
                                    type="email" 
                                    name="email-address"  
                                    id="email-address"
                                />
                            </div>
                            <div className="mt3">
                                <label className="db fw4 lh-copy f6" htmlFor="password">Password</label>
                                <input className="b pa2 input-reset ba bg-transparent" 
                                    type="password" 
                                    name="password"  
                                    id="password"
                                />
                            </div>
                            </fieldset>
                            <div className="mt3">
                            <input className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6" 
                            type="submit" value="Register"/>
                            </div>
                    </div>
                </main>
            </article>
        );
    }
}

export default Register;
