import React from 'react'
// import 'font-awesome/css/font-awesome.min.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faCheck, faThumbsUp } from '@fortawesome/free-solid-svg-icons'
// var FontAwesome = require('@fortawesome/react-fontawesome')


export class Dropdown extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isListOpen: false,
            headerTitle: this.props.title
        }
    }
    toggleList = () => {
        this.setState(prevState => ({
            isListOpen: !prevState.isListOpen
        }))
    }
    selectItem = (item) => {
        const { resetThenSet } =this.props;
        const { name, id, } = item;

        this.setState({
            headerTitle: name,
            isListOpen: false,
        }, () => resetThenSet(id,))
    }
    render (){
        const { isListOpen, headerTitle } = this.state
        const {countries} = this.props
        return (
            <div className=" btn btn-success form-control pa3 ma3 flex-row form-control p-2 bd-highlight">
                <div 
                    type="button"
                    className=""
                    onClick={this.toggleList}>
                        <div className=" flex-row">{headerTitle}</div>
                        {isListOpen
                        ? <FontAwesomeIcon  className="angle-up" size="50x"/>
                        :<FontAwesomeIcon className="angle-down" size="2x"/>}
                    </div>
                    {isListOpen && (
                        <div className="" role="list">
                            {countries.map((item) => (
                                <div className="container-fluid pa2 ma2 position-relative flex-row list-group form-control" aria-label="Default select example"
                                    type="button"
                                    key={item.id}
                                    onClick={() => this.selectItem(item)}>
                                        {item.name}
                                        {' '}
                                        {item.selected }
                                </div>
                            ))}
                        </div>
                    )}
            </div>
        )
    }
}
export default Dropdown;
